export declare const id: <A>(x: A) => A;
export declare const compose: <A, B, C>(f: (b: B) => C, g: (a: A) => B) => (x: A) => C;
export declare const apply: <A, B>(f: (a: A) => B, x: A) => B;
export interface Curried2<A, B, C> {
    (): Curried2<A, B, C>;
    (a: A, b: B): C;
    (a: A): (b: B) => C;
}
export declare function curry2<A, B, C>(f: (a: A, b: B) => C): Curried2<A, B, C>;
export interface Curried3<A, B, C, D> {
    (): Curried3<A, B, C, D>;
    (a: A): Curried2<B, C, D>;
    (a: A, b: B): (c: C) => D;
    (a: A, b: B, c: C): D;
}
export declare function curry3<A, B, C, D>(f: (a: A, b: B, c: C) => D): Curried3<A, B, C, D>;
export interface Curried4<A, B, C, D, E> {
    (): Curried4<A, B, C, D, E>;
    (a: A): Curried3<B, C, D, E>;
    (a: A, b: B): Curried2<C, D, E>;
    (a: A, b: B, c: C): (d: D) => E;
    (a: A, b: B, c: C, d: D): E;
}
export declare function curry4<A, B, C, D, E>(f: (a: A, b: B, c: C, d: D) => E): Curried4<A, B, C, D, E>;
